import React, { useState } from 'react';
import ScrollImages from './ScrollImages';
import './AuthModal.css';

const offerMeals = [
  {
    image: "https://images.unsplash.com/photo-1600891964599-f61ba0e24092",
    name: "Paneer Masala",
    mess: "Sai Krupa Mess",
    info: "Delicious spicy paneer with roti and salad",
    rating: "⭐⭐⭐⭐ 4.5"
  },
  {
    image: "data:image/jpeg;base64,",
    name: "Pav Bahji",
    mess: "Aai Mess",
    info: "Famous Mumbai style crispy pav Bhaji",
    rating: "⭐⭐⭐⭐⭐ 5.0"
  },
  {
    image: "data:image/jpeg;base64,",
    name: "Thalipeeth",
    mess: "Annapurna Tiffin",
    info: "Healthy multigrain thalipeeth with chutney",
    rating: "⭐⭐⭐ 3.8"
  }
];

const AuthModal = ({ offer, onClose }) => {
  const [isSignUp, setIsSignUp] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [cart, setCart] = useState({});

  const handleToggle = () => {
    setIsSignUp(!isSignUp);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsAuthenticated(true);
  };

  const handleAdd = (name) => {
    setCart(prev => ({
      ...prev,
      [name]: (prev[name] || 0) + 1
    }));
  };

  const handleRemove = (name) => {
    setCart(prev => ({
      ...prev,
      [name]: Math.max((prev[name] || 0) - 1, 0)
    }));
  };

  return (
    <div className="auth-modal-overlay">
      <ScrollImages />
      <div className="auth-modal">
        <button className="close-button" onClick={onClose}>✖</button>
        <h1 className="swadbite-logo">SwadBite!! </h1>

        {!isAuthenticated ? (
          <>
            <h2 className="auth-heading">{isSignUp ? 'Create Your Account' : 'Login to Continue'}</h2>
            <p className="offer-text">To avail: <strong>{offer.title}</strong></p>

            <form className="auth-form" onSubmit={handleSubmit}>
              <input type="text" placeholder="Username" required />
              {isSignUp && <input type="email" placeholder="Email" required />}
              <input type="password" placeholder="Password" required />
              <button type="submit">{isSignUp ? 'Sign Up' : 'Sign In'}</button>
            </form>

            <div className="toggle-text">
              {isSignUp ? 'Already have an account?' : "Don't have an account?"}
              <span onClick={handleToggle}>
                {isSignUp ? ' Sign In' : ' Create New Account'}
              </span>
            </div>
          </>
        ) : (
          <div className="meal-info">
            <h2 className="meal-heading">Offer Meals : {offer.title}</h2>
            <div className="meal-list">
              {offerMeals.map(meal => (
                <div className="meal-card" key={meal.name}>
                  <img src={meal.image} alt={meal.name} />
                  <div className="meal-details">
                    <h3>{meal.name}</h3>
                    <p><b>Mess:</b> {meal.mess}</p>
                    <p>{meal.info}</p>
                    <p>{meal.rating}</p>
                    <div className="cart-controls">
                      <button onClick={() => handleRemove(meal.name)}>-</button>
                      <span>{cart[meal.name] || 0}</span>
                      <button onClick={() => handleAdd(meal.name)}>+</button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <button className="order-now-btn"> Order Now</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AuthModal;