const Explore = () => {
  useEffect(() => {
    AOS.init({ duration: 1000, once: true });
  }, []);
  const navigate = useNavigate();

  // Get selected city and locality from localStorage
  const selectedCity = localStorage.getItem("selectedCity")?.trim().toLowerCase();
  const selectedLocality = localStorage.getItem("selectedLocality")?.trim().toLowerCase();

  // Filter messes by city and locality if selected
  const filteredMesses = messes.filter(mess => {
    const location = mess.location.toLowerCase();
    // If both city and locality are selected, filter by both
    if (selectedCity && selectedLocality) {
      return location.includes(selectedCity) && location.includes(selectedLocality);
    }
    // If only city is selected, filter by city
    if (selectedCity) {
      return location.includes(selectedCity);
    }
    // If only locality is selected, filter by locality
    if (selectedLocality) {
      return location.includes(selectedLocality);
    }
    // If nothing is selected, show all
    return true;
  });

  return (
    <>
      <Navbar />
      <FoodCarousel />

      <div className="explore-tagline" data-aos="fade-up">
        📍 Discover the Best Messes Around Pune
      </div>

      <div className="explore-container">
        {filteredMesses.map((mess, index) => {
          const column = index % 3;
          const animation =
            column === 0 ? "fade-right" : column === 1 ? "fade-up" : "fade-left";

          return (
            <div key={index} className="explore-card" onClick={() => {navigate("/WeeklyMenu")}} data-aos={animation}>
              <img src={mess.image} alt={mess.name} />
              <div className="explore-card-content">
                <div className="explore-mess-name">{mess.name}</div>
                <div className="explore-mess-location">{mess.location}</div>
                <div className="rating">{mess.rating}</div>
                <a
                  className="explore-map-link"
                  href={mess.map}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  📍 View on Map
                </a>
              </div>
            </div>
          );
        })}
      </div>

      <Offers />
    </>
  );
};

export default Explore;
