import React, { useState } from 'react';
import './Offers.css';
import AuthModal from './AuthModal';

const offerData = [
  { title: ' 25% off on 1st Order', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS43LX2q57i3nzOfyR-cy92XK9a1w27buoltw&s' },
  { title: ' Combo Meal @99', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr6G3j9i5DAoP_35R9Z-UGrgo-w6XeeVyVvg&s' },
  { title: ' Weekend Special Thali', image: 'data:image/jpeg;base64,' },
  { title: ' Unlimited Rice Day!', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRa-xPcI4FZItZurr7mS6ldRbPJoSoES3qH2g&s' },
  { title: ' Free Drink with Every Meal', image: 'data:image/jpeg;base64,' },
  { title: ' Flat ₹50 off above ₹300', image: 'data:image/png;base64,' },
];

const Offers = () => {
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [showAuth, setShowAuth] = useState(false);

  const handleOfferClick = (offer) => {
    setSelectedOffer(offer);
    setShowAuth(true);
  };

  return (
    <div className="offers-section">
      <h2 className="offer-heading"> Special Offers for You !!</h2>

      <div className="offer-cards">
        {offerData.map((offer, index) => (
          <div
            key={index}
            className="offer-card"
            data-aos="zoom-in"
            onClick={() => handleOfferClick(offer)}
          >
            <img src={offer.image} alt={offer.title} />
            <p>{offer.title}</p>
          </div>
        ))}
      </div>

      {showAuth && (
        <AuthModal
          offer={selectedOffer}
          onClose={() => setShowAuth(false)}
        />
      )}
    </div>
  );
};

export default Offers;